import React from 'react';

// Define the styles object
const styles = {
    container: {
        marginTop: '20px',
        borderTop: '1px solid #0ff',
        paddingTop: '10px',
        color: '#f5f5f5',
        textShadow: '0 0 10px #0ff, 0 0 20px #0ff',
        fontFamily: 'Arial, sans-serif'
    },
    heading: {
        fontSize: '24px',
        color: '#ff00ff', /* Neon Pink */
        textShadow: '0 0 10px #ff00ff, 0 0 20px #ff00ff'
    },
    list: {
        listStyleType: 'none',
        padding: 0
    },
    listItem: {
        marginBottom: '10px'
    }
};

// Define the MovementRules component and apply the styles
const MovementRules = () => {
    return (
        <div style={styles.container}>
            <h2 style={styles.heading}>Movement Rules</h2>
            <ul style={styles.list}>
                <li style={styles.listItem}><strong>Pawn (P):</strong> Moves 1 step in any direction (L, R, F, B).</li>
                <li style={styles.listItem}><strong>Hero1 (H1):</strong> Moves 2 steps straight in any direction (L, R, F, B). Kills any opponent's character in its path.</li>
                <li style={styles.listItem}><strong>Hero2 (H2):</strong> Moves 2 steps diagonally in any direction (FL, FR, BL, BR). Kills any opponent's character in its path.</li>
                <li style={styles.listItem}><strong>Hero3 (H3):</strong> Moves 2 steps straight and 1 step to the side in any direction (FL, FR, BL, BR, RF, RB, LF, LB). Kills only the character at its final landing position.</li>
            </ul>
        </div>
    );
};

export default MovementRules;
